mapper.select:
SELECT sno, sname, cno, cname
FROM article_info
WHERE sno=#{sno} and cno=#{cno}

INSERT into k_article(no, sno, cno, title, content, img_url, user_id)
FROM k_article
WHERE no=#{no}

mapper.update:
UPDATE k_article
SET count = count + 1 
WHERE no=#{no}

UPDATE k_article
SET sno=#{sno}, cno=#{cno}, title=#{title}, content=#{content}, img_url=#{img_url}
WHERE no=#{no}
